import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HostLogin from './components/Host/HostLogin';
import HostRegistration from './components/Host/HostRegistration';
import './App.css';

function App() {
  return (
    <Router>
      <Routes>
        {/* Define the route for the login page */}
        <Route path="/login" element={<HostLogin />} />
        
        {/* Define the route for the registration page */}
        <Route path="/register" element={<HostRegistration />} />
        
        {/* Optionally, redirect from the root URL to the login page */}
        <Route path="/" element={<HostLogin />} />
      </Routes>
    </Router>
  );
}

export default App;
